<?phpfunction getHash($value){    $value=func_get_args();  //返回全部参数的值,类型是数组    return md5(md5(implode(",",$value)));}function getSaltHash($value){    $salt=mt_rand(10000,100000);    return md5($value.$salt);}function getSafeHash($value, $authKey='auth'){    $auth=getConfig('password', $authKey);    return sha1($auth.$value.$auth);}function getPassWord($user, $pass){    $auth=getConfig('password', 'auth');    return sha1($auth.$user.$auth.$pass.$auth);}